import { useState, useEffect } from "react";
import { useLocation, useParams } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Search, UserPlus } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useGame } from "@/lib/game-context";
import { z } from "zod";

const PLAYER_NAME_KEY = "spy_game_player_name";

const joinSchema = z.object({
  playerName: z.string().min(2, "الاسم يجب أن يكون حرفين على الأقل").max(20, "الاسم طويل جداً"),
});

type JoinInput = z.infer<typeof joinSchema>;

export default function Join() {
  const { roomId } = useParams<{ roomId: string }>();
  const { joinRoom, isConnected, error, room } = useGame();
  const [, setLocation] = useLocation();
  const [isJoining, setIsJoining] = useState(false);

  const savedName = typeof window !== "undefined" ? localStorage.getItem(PLAYER_NAME_KEY) || "" : "";

  const form = useForm<JoinInput>({
    resolver: zodResolver(joinSchema),
    defaultValues: {
      playerName: savedName,
    },
  });

  const handleJoin = (data: JoinInput) => {
    if (!roomId) return;
    const trimmedName = data.playerName.trim();
    const normalizedRoomCode = roomId.trim().toUpperCase();
    localStorage.setItem(PLAYER_NAME_KEY, trimmedName);
    setIsJoining(true);
    joinRoom(trimmedName, normalizedRoomCode);
  };

  useEffect(() => {
    if (room) {
      setLocation(`/room/${room.id}`);
    }
  }, [room, setLocation]);

  if (room) {
    return null;
  }

  return (
    <div className="min-h-screen bg-background flex flex-col items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <div className="flex justify-center mb-4">
            <div className="relative">
              <Search className="w-16 h-16 text-primary" />
              <div className="absolute -top-1 -right-1 w-6 h-6 bg-spy rounded-full flex items-center justify-center">
                <span className="text-spy-foreground text-xs font-bold">؟</span>
              </div>
            </div>
          </div>
          <CardTitle className="text-2xl">الانضمام للغرفة</CardTitle>
          <p className="text-muted-foreground mt-2">
            أنت مدعو للانضمام لغرفة <span className="font-mono font-bold">{roomId}</span>
          </p>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(handleJoin)} className="space-y-6">
              <FormField
                control={form.control}
                name="playerName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>اسمك</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="أدخل اسمك..."
                        data-testid="input-player-name-join-link"
                        autoFocus
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              {error && (
                <p className="text-sm text-destructive">{error}</p>
              )}
              <Button 
                type="submit" 
                className="w-full min-h-12 gap-2" 
                disabled={!isConnected || isJoining}
                data-testid="button-submit-join-link"
              >
                <UserPlus className="w-5 h-5" />
                {isJoining ? "جاري الانضمام..." : "انضمام للغرفة"}
              </Button>
              {!isConnected && (
                <p className="text-center text-sm text-muted-foreground animate-pulse">
                  جاري الاتصال بالخادم...
                </p>
              )}
            </form>
          </Form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/game/pre-voting-tr.content:
import { useEffect, useState } from "react";
import { Clock, Vote } from "lucide-react";
import { Card, CardContent } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { useGame } from "@/lib/game-context";

const TRANSITION_DURATION = 10; // 10 seconds

export function PreVotingTransition() {
  const { room, timerRemaining } = useGame();
  const [displayTimer, setDisplayTimer] = useState(TRANSITION_DURATION);

  // Update display timer whenever server timer changes
  useEffect(() => {
    if (room?.phase === "pre_voting_transition" && timerRemaining > 0) {
      console.log(`PreVotingTransition: Server timer update: ${timerRemaining}s`);
      setDisplayTimer(timerRemaining);
    }
  }, [timerRemaining, room?.phase]);

  if (!room) return null;

  const lastQuestion = room.questions && room.questions.length > 0 
    ? room.questions[room.questions.length - 1] 
    : null;
  const asker = lastQuestion ? room.players.find((p) => p.id === lastQuestion.askerId) : null;
  const target = lastQuestion ? room.players.find((p) => p.id === lastQuestion.targetId) : null;

  return (
    <div className="space-y-6">
      <div className="text-center space-y-4">
        <h1 className="text-2xl md:text-3xl font-bold text-foreground animate-fade-in">
          انتهت مرحلة الأسئلة
        </h1>
        <p className="text-muted-foreground">
          جاري الانتقال إلى مرحلة التصويت...
        </p>

        <div className="mt-6">
          <div className="flex items-center justify-center gap-2 mb-3">
            <Vote className="w-6 h-6 text-primary animate-pulse" />
            <span className="text-3xl font-bold tabular-nums text-primary">
              {displayTimer}
            </span>
          </div>
          <Progress 
            value={(displayTimer / TRANSITION_DURATION) * 100} 
            className="h-3 w-64 mx-auto"
          />
        </div>
      </div>

      {lastQuestion && (
        <Card className="border-primary/50 shadow-lg">
          <CardContent className="p-6">
            <p className="text-sm text-muted-foreground mb-3 text-center">
              آخر سؤال:
            </p>
            <div className="space-y-4">
              <div className="p-4 rounded-lg bg-muted/50">
                <p className="text-sm text-muted-foreground mb-1">
                  <span className="font-medium">{asker?.name}</span> سأل{" "}
                  <span className="font-medium">{target?.name}</span>:
                </p>
                <p className="font-medium mt-2">{lastQuestion.question}</p>
              </div>
              {lastQuestion.answer && (
                <div className="p-4 rounded-lg bg-accent/10 border border-accent/20">
                  <p className="text-sm text-muted-foreground mb-1">
                    <span className="font-medium">{target?.name}</span> أجاب:
                  </p>
                  <p className="font-medium mt-2">{lastQuestion.answer}</p>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      )}

      <div className="text-center">
        <div className="inline-flex items-center gap-2 text-muted-foreground animate-pulse">
          <Clock className="w-5 h-5" />
          <p>استعد للتصويت على من تعتقد أنه الجاسوس</p>
        </div>
      </div>
    </div>
  );
}
